import React, { useEffect, useState } from "react";
import "./styles.css";
import { useDispatch, useSelector } from "react-redux";
import {
  removeFromCart,
  selectItems,
  selectTotal,
} from "../../slices/cartSlice";
import { selectUser } from "../../slices/userSlice";
import Signin from "../Signin/Signin";
import { loadStripe } from "@stripe/stripe-js";
import { useHistory } from "react-router";
import axios from "axios";
import { useElements, useStripe } from "@stripe/react-stripe-js";
import StripeContainer from "../Checkout/StripeContainer";

// const stripePromise = loadStripe(`${process.env.STRIPE_PUBLIC_KEY}`);
const stripePromise = loadStripe(
  "pk_test_51J1YVxL3fRcUBWQKeGVALCUqUUO53Mrrpdw5Dg7d4rMJGPhFJ16XfHAcM0dwsH4GUPC8XzqQcqvFCQ1Oo2CSlMs0007bvipQ9x"
);

const CARD_OPTIONS = {
  iconStyle: "solid",
  style: {
    base: {
      iconColor: "#c4f0ff",
      color: "#fff",
      fontWeight: 500,
      fontFamily: "Roboto, Open Sans, Segoe UI, sans-serif",
      fontSize: "16px",
      fontSmoothing: "antialiased",
      ":-webkit-autofill": { color: "#fce883" },
      "::placeholder": { color: "#87bbfd" },
    },
    invalid: {
      iconColor: "#ffc7ee",
      color: "#ffc7ee",
    },
  },
};

function Cart({ open, setOpen }) {
  const history = useHistory();

  //To display cart items
  const items = useSelector(selectItems);

  //To display total price
  const total = useSelector(selectTotal);

  //To remove item from cart
  const dispatch = useDispatch();

  const removeItem = (id) => {
    dispatch(removeFromCart({ id }));
  };

  //For logging out
  const user = useSelector(selectUser);

  //Showing sign in modal --> in order to checkout
  const [openModal, setOpenModal] = useState(false);

  const showModal = () => {
    setOpenModal(!openModal);
  };

  const createCheckout = async (event) => {
    // Get Stripe.js instance
    const stripe = await stripePromise;

    // Call your backend to create the Checkout Session
    const response = await axios.post(
      "http://localhost:5000/api/create-checkout-session",
      {
        items: items,
      }
    );
  };

  //   const session = await response.json();

  //   // When the customer clicks on the button, redirect them to Checkout.
  //   const result = await stripe.redirectToCheckout({
  //     sessionId: session.id,
  //   });

  //   if (result.error) {
  //     console.log("ERROR");
  //   }
  // };

  const toCheckout = () => {
    history.push("/create-checkout-session");
  };

  return (
    <div className="cart__container">
      <div className="cart__wrapper">
        <div className="cart__header">
          <h1>Your Cart</h1>
          <p className="close--icon" onClick={() => setOpen(!open)}>
            x
          </p>
        </div>

        {/* Cart Items */}
        <div className="item--wrapper">
          {items.map((item) => (
            <div className="single__product--wrapper" key={item.id}>
              <img className="product_img" src={item.image} alt="product_img" />
              <div className="product__details">
                <p className="product_name">{item.name}</p>
                <p className="product_price"> ${item.price} </p>
                <div className="product__settings">
                  <div className="product__counter">
                    <button className="cart__button">-</button>
                    <p>1</p>
                    <button className="cart__button">+</button>
                  </div>
                  <p onClick={() => removeItem(item.id)}>REMOVE</p>
                </div>
              </div>
              <h2 className="product__price">${item.price}</h2>
            </div>
          ))}
        </div>

        <div className="subtotal">
          <p>Sub-total: </p>
          <p>${total}</p>
        </div>

        {/* Checkout button */}
        <button
          to={user ? "/create-checkout-session" : null}
          onClick={user ? createCheckout : () => showModal()}
          // onClick={user ? ()=>toCheckout() : () => showModal()}
          className="checkout--button"
        >
          {user ? "Checkout" : "Sign In To Checkout"}
        </button>

        {openModal ? (
          <Signin openModal={openModal} setOpenModal={setOpenModal} />
        ) : null}
      </div>
    </div>
  );
}

export default Cart;
