import React from 'react';
import { useDispatch } from "react-redux";
import './styles.css';
import { addToCart } from "../../slices/cartSlice";

function Popular() {

  const dispatch = useDispatch();

  const addItem = (id, name, price, description, image) => {
    const product = {
      id,
      name,
      price,
      description,
      image
    };

    //Sending the product as an action to the REDUX store
    dispatch(addToCart(product))
  }

  return (
    <div className ="popular__container">
      <div className="items__wrapper">
        <div className="items--contents">
          {proteins.map((protein) => {
            return(
            <div className="products--container" key={protein.id}>
              <div className = "image--wrapper">
                <img className="protein--image" src={protein.image}></img>
              </div>
              <div className = "description">
                <h1 className="tagline"> FOR YOUR WORKOUTS </h1>
                <h2 className="price"> ${protein.price}</h2>
                <p className="product-desc">{protein.description}</p>
                <a href='/' className="learnMore">Learn More</a>
                <div className = "buttons">
                    <button type="submit" onClick={() => addItem(protein.id, protein.name, protein.price, protein.description, protein.image) } className="buy">Buy</button>
                  <button type = "submit" className="subscribe">Suscribe $75/month</button>
                </div>
              </div>
              </div>
            )
          })}
        </div>
        <h1 className="bars__h1">THE <span>PERFECT</span> COMBINATION</h1>
      </div>
    </div>
  )
}

export default Popular
