import { Elements } from "@stripe/react-stripe-js";
import { loadStripe } from "@stripe/stripe-js";
import React, { useState } from "react";
import { useSelector } from "react-redux";
import { Icon, Popup } from "semantic-ui-react";
import { selectItems } from "../../slices/cartSlice";
import { selectUser } from "../../slices/userSlice";
import Cart from "../Cart/Cart";
import Signin from "../Signin/Signin";
import Signout from "../Signout/Signout";
import "./styles.css";
import { Parallax } from "react-parallax";
import { useSpring, animated } from "react-spring";
import useMeasure from "./useMeasure";

const PUBLIC_KEY =
  "pk_test_51J1YVxL3fRcUBWQKeGVALCUqUUO53Mrrpdw5Dg7d4rMJGPhFJ16XfHAcM0dwsH4GUPC8XzqQcqvFCQ1Oo2CSlMs0007bvipQ9x";

const stripeTestPromise = loadStripe(PUBLIC_KEY);

function Hero() {
  const user = useSelector(selectUser);

  //For opening and closing the cart
  const [open, setOpen] = useState(false);
  const HandleOpen = () => {
    setOpen(!open);
    toggle(!open)
  };

  //select from the items, take the length and render it on top of the cart icon
  const items = useSelector(selectItems);

  //Showing signin/register modal
  const [openModal, setOpenModal] = useState(false);

  const showModal = () => {
    setOpenModal(!openModal);
  };

  //To show a pop up showing user email
  const [showUser, setShowUser] = useState(false);

  //For opening cart effect
  const [toggle, setToggle] = useState(false);
  const [bind, { width }] = useMeasure();
  const props = useSpring({
    width: toggle ? width : 0,
    backgroundColor: toggle ? "hotpink" : "turquoise",
    config: { duration: 300 },
  });

  return (
    <div className="Hero__Wrapper">
      <Parallax
        bgImage="https://images.unsplash.com/photo-1583248839364-bd7430a3cd83?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1651&q=80"
        strength={500}
        style={{
          height: "100vh",
          backgroundAttachment: "fixed",
          overflow: "hidden",
        }}
      >
        <div className="Background__Overlay">
          {open ? (
            <Elements stripe={stripeTestPromise}>
              <Cart {...bind} open={open} setOpen={setOpen} />
            </Elements>
          ) : null}
          <div className="Nav__Wrapper">
            <div className="Hero__Nav">
              <div className="Hero__Links__1">
                <a href="/" className="shop">
                  Shop
                </a>
                <a href="/" className="posts">
                  Posts
                </a>
                <a href="/" className="about">
                  About us
                </a>
              </div>
              <div className="Hero__Links__2">
                <Icon
                  onMouseOver={() => setShowUser(true)}
                  onMouseLeave={() => setShowUser(false)}
                  onClick={showModal}
                  name="user outline"
                />
                {openModal && !user ? (
                  <Signin openModal={openModal} setOpenModal={setOpenModal} />
                ) : (
                  <Signout openModal={openModal} setOpenModal={setOpenModal} />
                )}

                {showUser ? (
                  <div className="user__popup">
                    {user ? (
                      <p>Logged in as {user.email}</p>
                    ) : (
                      <p> Please sign in </p>
                    )}
                  </div>
                ) : null}

                <span className="count"> {items ? items.length : 0} </span>
                <Icon onClick={HandleOpen} name="shopping cart" />
              </div>
            </div>
          </div>
          <h1 className="Tagline">
            The only bad workout is the one you didn’t do.
          </h1>
        </div>
      </Parallax>
    </div>
  );
}

export default Hero;
