import React from 'react';
import { useDispatch } from "react-redux";
import './styles.css';
import products from '../../items/products'
import { addToCart } from "../../slices/cartSlice";

function Popular({ id, title, price, description, image }) {

  const dispatch = useDispatch();

  const addItem = () => {
    const product = {
      id,
      title,
      price,
      description,
      image
    };

    //Sending the product as an action to the REDUX store
    dispatch(addToCart(product))
  }

  return (
    <div className ="popular__container">
      <div className="items__wrapper">
        <div className="items--contents">
          {products.map((products) => {
            return(
            <div className="products--container">
              <div className = "image--wrapper">
                <img className="protein--image" src={products.image}></img>
              </div>
              <div className = "description">
                <h1 className="tagline"> FOR YOUR WORKOUTS </h1>
                <h2 className="price"> {products.price}</h2>
                <p className="product-desc">{products.description}</p>
                <a href='/' className="learnMore">Learn More</a>
                <div className = "buttons">
                    <button type="submit" onClick={ addItem }className="buy">Buy</button>
                  <button type = "submit" className="subscribe">Suscribe $75/month</button>
                </div>
              </div>
              </div>
            )
          })}
        </div>
      </div>
    </div>
  )
}

export default Popular
