import React, { useEffect, useState } from "react";
import "./styles.css";
import { useDispatch, useSelector } from "react-redux";
import {
  removeFromCart,
  selectItems,
  selectTotal,
} from "../../slices/cartSlice";
import { selectUser } from "../../slices/userSlice";
import Signin from "../Signin/Signin";
import { CardElement, Elements, useElements, useStripe } from "@stripe/react-stripe-js";
import { loadStripe } from "@stripe/stripe-js";

const stripePromise = loadStripe(`${process.env.PUBLISHABLE_KEY}`);

function Cart({ open, setOpen }) {
  //To display cart items
  const items = useSelector(selectItems);

  //To display total price
  const total = useSelector(selectTotal);

  //To remove item from cart
  const dispatch = useDispatch();

  const removeItem = (id) => {
    dispatch(removeFromCart({ id }));
  };

  //For logging out
  const user = useSelector(selectUser);

  //Showing sign in modal --> in order to checkout
  const [openModal, setOpenModal] = useState(false);

  const showModal = () => {
    setOpenModal(!openModal);
  };

  const stripe = useStripe();
  const elements = useElements();

  const handleSubmit = async (event) => {
    // Block native form submission.
    event.preventDefault();

    if (!stripe || !elements) {
      // Stripe.js has not loaded yet. Make sure to disable
      // form submission until Stripe.js has loaded.
      return;
    }

    // Get a reference to a mounted CardElement. Elements knows how
    // to find your CardElement because there can only ever be one of
    // each type of element.
    const cardElement = elements.getElement(CardElement);

    // Use your card Element with other Stripe.js APIs
    const { error, paymentMethod } = await stripe.createPaymentMethod({
      type: "card",
      card: cardElement,
    });

    if (error) {
      console.log("[error]", error);
    } else {
      console.log("[PaymentMethod]", paymentMethod);
    }
  };

  return (
    <div className="cart__container">
      <div className="cart__wrapper">
        <div className="cart__header">
          <h1>Your Cart</h1>
          <p className="close--icon" onClick={() => setOpen(!open)}>
            {" "}
            x
          </p>
        </div>

        {/* Cart Items */}
        <div className="item--wrapper">
          {items.map((item) => (
            <div className="single__product--wrapper" key={item.id}>
              <img className="product_img" src={item.image} alt="product_img" />
              <div className="product__details">
                <p className="product_name">{item.name}</p>
                <p className="product_price"> ${item.price} </p>
                <div className="product__settings">
                  <div className="product__counter">
                    <button className="cart__button">-</button>
                    <p>1</p>
                    <button className="cart__button">+</button>
                  </div>
                  <p onClick={() => removeItem(item.id)}>REMOVE</p>
                </div>
              </div>
              <h2 className="product__price">{item.price}</h2>
            </div>
          ))}
        </div>

        <div className="subtotal">
          <p>Sub-total: </p>
          <p>${total}</p>
        </div>

        {/* Checkout button */}
        <Elements stripe={stripePromise}>
          <button
            onClick={user ? null : () => showModal()}
            className="checkout--button"
          >
            {user ? "Checkout" : "Sign In To Checkout"}
          </button>
          {openModal ? (
            <Signin openModal={openModal} setOpenModal={setOpenModal} />
          ) : (
            <CardElement
              options={{
                style: {
                  base: {
                    fontSize: "16px",
                    color: "#424770",
                    "::placeholder": {
                      color: "#aab7c4",
                    },
                  },
                  invalid: {
                    color: "#9e2146",
                  },
                },
              }}
            />
          )}
        </Elements>
      </div>
    </div>
  );
}

export default Cart;
